/* utility for handling ANSI escape sequences

	Drawn from : http://en.wikipedia.org/wiki/ANSI_escape_code#CSI_codes
 */
var CSI = module.exports = {
	apply : function(args) {
		this.write('\u001B[' + args);
	},
	/** Write to console */
	write : function(cmd) {
		process.stdout.write(cmd);
	},
/** Move the cursor to given line, column
	
	CSI.move(1,4); 	//Move cursor to line 1, column 4
	CSI.move(10); 	//Move cursor to line 10

 */
	move : function(line, col) {	
		this.apply(line + ';' + (col || 1) + 'f');
	},
/** Hide the cursor */
	hide : function() {
		this.apply('?25l');
	},
	/** Show the cursor */
	show: function() {
		this.apply('?25h');
	},
	/** Clear part of the screen

		Clears entire screen If line is omitted
		Clears line if col is omitted
	*/
	clear : function(line) {
		if(typeof line === 'number') {
			this.move(line);
			this.apply('2K');
		} else {
			this.apply('2J');
		}
	}
}
